"use strict";

var mockedModules = require("./"),
    _require = require("chai"),
    expect = _require.expect;


describe("mockedModules", function () {
  it("method wrap should wrap code correctly", function () {
    console.log("wat");
    var code = "return [exports, require, module, __filename, __dirname];";

    var args = [1, 2, 3, 4, 5];

    var result = eval(mockedModules.native_module.wrap(code)).apply(null, args);

    var l = args.length;
    for (var i = 0; i < l; i++) {
      expect(result[i]).to.be.equal(args[i]);
    }
  });
});